const Post = (props)=>{
    let image=''

    if (props.art.promo_items){
        if(props.art.promo_items.basic.resized_urls){
            image=<img src={props.art.promo_items.basic.resized_urls[2]} alt={props.art.promo_items.basic.subtitle} />
            console.log(props.art.promo_items.basic.resized_urls[2])
        }else
            image= <img className="image" src={props.art.promo_items.basic.url} alt={props.art.promo_items.basic.subtitle}/>
    }
    
 

    return(
        <div>
            <article>
                {/* Image */}
                <div>
                    {
                        image
                    }
                </div>
                {/* Content text */}
                <div>
                    <h1>
                        {props.art.headlines.basic}
                    </h1> 
                </div>
            </article>
            <style jsx>
                {`
                    h1{
                        color:yellow
                    }
                    img{
                        max-width:171px
                    }
                `}
            </style>
        </div>
    
    )
}


export default Post
