import Post from './Post'

const Posts=(props)=>(
    <div className="posts">
        {
            props.articles.map(article=>(
                <Post 
                    key={article._id}
                    art={article}
                />
            ))
        }

        <style global jsx>
            {`
                .posts{
                    display:grid;
                    grid-template-columns:repeat(12,1fr)
                }

                .post{
                    grid-columns:span 3;
                }
            `}
        </style>
    </div>
)

export default Posts
