import {useState} from 'react'
const Tags =(props)=>{
    const [state,setState]=useState(props.tags)
    const updateState=(tag)=>{
        setState({
            ...state,
            tags:tag
        })
    }
    for (const art of props.articles) {
        for (const tag of art.taxonomy.tags) {
            console.log(tag.text)
            updateState(tag.text)
        }
        console.log(art.taxonomy.tags)
    }
    return(
        <>
            <p>TAGS</p>
        </>
    )
}

export default Tags;
