import Posts from '../components/Posts'
import Tags from '../components/Tags'
import fetch from 'isomorphic-unfetch'

const Index=(props)=>(
    <>
        <h1>Acumulado Grilla</h1>
        <Tags articles={props.articles} tags={props.tags}/>
        <div>
            <Posts articles={props.articles} />
            <aside><h1>ASIDE</h1></aside>
        </div>
     
        
    </>
)
const loadTags=()=>{
    for (const art of props.articles) {
        for (const tag of art.taxonomy.tags) {
            
            props.tags=tag.text
        }
    }
}
Index.getInitialProps=async(req)=>{
    const data= await fetch('https://api-test-ln.herokuapp.com/articles')
    const res = await data.json()
    loadTags
    return{
        articles:res.articles,
        tags:[]
    }

}


export default Index
