const Post = (props)=>{
    let image=props.art.promo_items.basic.url,
        altImage=props.art.promo_items.basic.subtitle
    return(
    
        <article>
            {/* Image */}
            <div>
                {
                    <img src={image} alt={altImage}/>
                }
            </div>
            {/* Content text */}
            <div>
                {props.art.headlines.basic}
            </div>
        </article>
    )
}


export default Post
