const Post = (props)=>{
    const image=''

    if(props.art.promo_items.basic.url!==''){
        image=<img src={props.art.promo_items.basic.url} alt={props.art.promo_items.basic.subtitle}/>
    }

    return(
    
        <article>
            {/* Image */}
            <div>
                {
                    // <img src={image} alt={altImage}/>
                }
            </div>
            {/* Content text */}
            <div>
                {props.art.headlines.basic}
            </div>
        </article>
    )
}


export default Post
