const Post = (props)=>{
    let image=''

    if (props.art.promo_items)
        image=<img src={props.art.promo_items.basic} alt={props.art.promo_items.subtitle}/>
    
 

    return(
    
        <article>
            {/* Image */}
            <div>
                <img src={props.art.promo_items.basic} alt={props.art.promo_items.subtitle}/>
                {
                    image
                }
            </div>
            {/* Content text */}
            <div>
                {props.art.headlines.basic}
            </div>
        </article>
    )
}


export default Post
