const Post = (props)=>{
    let image=''

    if (props.art.promo_items)
        image= <img className="image" src={props.art.promo_items.basic.url} alt={props.art.promo_items.basic.subtitle}/>
    
 

    return(
        <>
            <article>
                {/* Image */}
                <div>
                    {
                        image
                    }
                </div>
                {/* Content text */}
                <div>
                    {props.art.headlines.basic}
                </div>
            </article>
        </>
    
    )
}


export default Post
