const Post = (props)=>{
    let image=''

    if (props.art.promo_items)
        image= <img src={props.art.promo_items.basic.url} alt={props.art.promo_items.basic.subtitle}/>
    
 

    return(
        <>
            <article>
                {/* Image */}
                <div>
                    {
                        image
                    }
                </div>
                {/* Content text */}
                <div>
                    {props.art.headlines.basic}
                </div>
            </article>

            <style jsx>{`
                p {
                color: blue;
                }
                div {
                background: red;
                }
                @media (max-width: 600px) {
                div {
                    background: blue;
                }
                }
            `}</style>
        </>
    
    )
}


export default Post
